import { useState } from 'react';
import './Courses.scss';
import Courses from './data.json'
function TableCourses(){
    const [search, setSearch] = useState('')
return (
<div className='courses'>
    <input type="text" onChange={(e) => setSearch(e.target.value)} class="form-control search-course" placeholder="Search Course by Title"></input>
    <div className="row">
        {
            Courses.filter(course =>{
                return course.toLowerCase() === '' ? course : course.title.toLowerCase().includes(search);
            }).map(course =>(
                <div className="col-sm-6 col-md-4 col-lg-3">
                <div className="card item-card" >
                    <img className="card-img-top" src={course.image} alt="Card image cap" />
                    <div className="card-body">
                        <h5 className="card-title title">{course.title}</h5>
                        <p className="card-text card-description">{course.description}</p>
                        <div className='button-action'>
                            <a href="#" className="btn btn-warning">Edit</a>
                            <a href="#" className="btn btn-danger">Delete</a>
                        </div>
                    </div>
                </div>
        </div>
            ))
        }

    </div>
</div>
)
}

export default TableCourses
